import React from "react";
import { useState } from "react";
import axios from "axios";
import { useEffect } from "react";

const FetchName = () =>
    {
    var x=1
    var y=[]
    const [name,Setname]=useState([])
    var f=""
    var d=[]
    function fetch(e)
    {
        f=e.target.value
    }
    function FetchSearch()
    {
        axios
        .get(`https://finnhub.io/api/v1/search?q=${f}&token=cjmeg19r01qmdd9puljgcjmeg19r01qmdd9pulk0`)
        .then((response) =>
        {
            const {count}=response.data
            var x=1
            while(x!=count)
            {
                const {symbol}=response.data.result[x]
                const {description}=response.data.result[x]
                x=x+1
                y= y.concat(symbol)
                d=d.concat(description)
                console.log(d)
                Setname(y)
            }
            
        })
    
}

    return(<>
    
    <input type="text" onChange={fetch}/>
    <button onClick={FetchSearch}>Generate</button>
    {
        name.map((e)=>{
            return <h1>{e+"        "+d}</h1>
        })
    }
    
    </>)
}
  

export default FetchName
