import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { ChartCanvas, Chart } from 'react-stockcharts';
import { CandlestickSeries } from 'react-stockcharts/lib/series';
import { XAxis, YAxis } from 'react-stockcharts/lib/axes';
import { fitWidth } from 'react-stockcharts/lib/helper';
import { discontinuousTimeScaleProvider } from 'react-stockcharts/lib/scale';

class CandlestickChart extends Component {
  render() {
    const { data, width, ratio } = this.props;

    return (
      <ChartCanvas
        width={width}
        height={400}
        ratio={ratio}
        margin={{ left: 50, right: 50, top: 10, bottom: 30 }}
        seriesName="Price"
        data={data}
        type="hybrid"
        xAccessor={({ date }) => date}
        xScaleProvider={discontinuousTimeScaleProvider}
        xExtents={['date']}
      >
        <Chart id={1} yExtents={d => [d.high, d.low]}>
          <XAxis axisAt="bottom" orient="bottom" ticks={6} />
          <YAxis axisAt="left" orient="left" ticks={5} />
          <CandlestickSeries width={5} />
        </Chart>
      </ChartCanvas>
    );
  }
}

CandlestickChart.propTypes = {
  data: PropTypes.array.isRequired,
  width: PropTypes.number.isRequired,
  ratio: PropTypes.number.isRequired,
};

const CandlestickChartWithWidth = fitWidth(CandlestickChart);

class App extends Component {
  render() {
    // Sample candlestick data (replace with your own data)
    const data = [
      // Replace with your candlestick data
      { date: new Date('2023-08-01'), open: 100, high: 120, low: 90, close: 110 },
      { date: new Date('2023-08-02'), open: 110, high: 130, low: 100, close: 120 },
      { date: new Date('2023-08-03'), open: 120, high: 140, low: 110, close: 130 },
      // Add more data points as needed
    ];

    return (
      <div className="App">
        <h1>Candlestick Chart Example</h1>
        <CandlestickChartWithWidth data={data} />
      </div>
    );
  }
}

export default App;
