        const [symbl,setSym]=useState([])
        var li=[]
        var li2=[]
        axios
                .get(`https://finnhub.io/api/v1/search?q=aapl&token=cjmeg19r01qmdd9puljgcjmeg19r01qmdd9pulk0`)
        .then((response) =>
        {   var con=0
               const {count}= response.data
               console.log(count)
               while(con<count){
                const {description,symbol}= response.data.result[con]
                console.log(description)
                li.push(symbol)
                li2.push(description)
                setDes(li2)
                setSym(li)
                con=con+1
               }
                
        })
    }
    useEffect(() => {
        Inn()
      }, [])
        
        return(<>
        {
           symbl.map((x)=>{
            return <h1>{x}</h1>
           })
            
            }
       </>)
       
    }
    export default FetchSearch
